// Imports (include necessary modules)
import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import "./index.css";

const Signup = () => {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [mobileNumber, setMobileNumber] = useState("");
  const [usernameError, setUsernameError] = useState("");
  const [emailError, setEmailError] = useState("");
  const [passwordError, setPasswordError] = useState("");
  const [confirmPasswordError, setConfirmPasswordError] = useState("");
  const [mobileNumberError, setMobileNumberError] = useState("");
  const [res, setRes] = useState("");
  const navigate = useNavigate();

  const handleUsernameChange = (e) => {
    setUsername(e.target.value);
    setUsernameError(""); // Clear error message when user starts typing
  };

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
    setEmailError(""); // Clear error message when user starts typing
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
    setPasswordError(""); // Clear error message when user starts typing
  };

  const handleConfirmPasswordChange = (e) => {
    setConfirmPassword(e.target.value);
    setConfirmPasswordError(""); // Clear error message when user starts typing
  };

  const handleMobileNumberChange = (e) => {
    setMobileNumber(e.target.value);
    setMobileNumberError(""); // Clear error message when user starts typing
  };

  const validateEmail = (email) => {
    // Regular expression for basic email validation
    const re = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return re.test(email);
  };

  const validatePassword = (password) => {
    // Basic password validation: at least 6 characters
    return password.length >= 6;
  };

  const validateMobileNumber = (mobileNumber) => {
    // Basic mobile number validation: must be digits and at least 10 characters
    const re = /^\d{10,}$/;
    return re.test(mobileNumber);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    let formValid = true;

    // Validate username, email, password, confirmPassword, mobileNumber (similar to your current validation logic)

    if (formValid) {
      try {
        const response = await axios.post('http://localhost:8000/signup/', {
          username,
          email,
          password,
          mobile_number: mobileNumber
        });
        setRes("User signed up successfully");
        setUsername("");
        setEmail("");
        setPassword("");
        setConfirmPassword("");
        setMobileNumber("");
        navigate("/signin"); // Redirect to signin page after successful signup
      } catch (error) {
        if (error.response && error.response.data) {
          setRes(error.response.data.detail);
        } else {
          setRes("An error occurred. Please try again.");
        }
      }
    }
  };

  // Function to handle forgot password
  const handleForgotPassword = async () => {
    if (!mobileNumber || !validateMobileNumber(mobileNumber)) {
      setMobileNumberError("Invalid mobile number format");
      return;
    }

    try {
      await axios.post('http://localhost:8000/send-otp/', {
        mobile_number: mobileNumber
      });
      // Redirect to reset password page or show confirmation message
      setRes("OTP sent successfully. Proceed to reset password.");
      // Optionally, navigate to reset password page
      // navigate("/reset-password");
    } catch (error) {
      if (error.response && error.response.data) {
        setRes(error.response.data.detail);
      } else {
        setRes("An error occurred. Please try again.");
      }
    }
  };

  return (
    <div className="bg">
      <div className="card-container">
        {/* Your JSX for signup form */}
        <form className="form" onSubmit={handleSubmit}>
          {/* Your input fields for username, email, password, confirmPassword */}
          <div className="inner-form">
            <label className="label">MOBILE NUMBER</label>
            <input
              type="text"
              value={mobileNumber}
              onChange={handleMobileNumberChange}
              className="user-input"
              placeholder="Mobile Number"
            />
            {mobileNumberError && (
              <p className="error-message">{mobileNumberError}</p>
            )}
          </div>
          <p className="response-message">{res}</p>
          <button type="button" className="forgot-password-button" onClick={handleForgotPassword}>
            Forgot Password
          </button>
          {/* Your signup button and signin link */}
        </form>
      </div>
    </div>
  );
};

export default Signup;
