import React, { Component } from "react";
import { Link, Navigate } from "react-router-dom";
import axios from "axios";
import "./index.css";

class SignIn extends Component {
  state = {
    us: "",
    pd: "",
    showSubmitError: false,
    errorMsg: "",
    submitForm: false,
    usernameError: "",
    passwordError: "",
  };

  onSubmitForm = async (event) => {
    event.preventDefault();

    const { us, pd } = this.state;

    // Reset error messages
    this.setState({ usernameError: "", passwordError: "", showSubmitError: false });

    // Validate inputs
    if (!us.trim() && !pd.trim()) {
      this.setState({ usernameError: "Username is required", passwordError: "Password is required" });
      return;
    } else if (!us.trim()) {
      this.setState({ usernameError: "Username is required" });
      return;
    } else if (!pd.trim()) {
      this.setState({ passwordError: "Password is required" });
      return;
    }

    try {
      const response = await axios.post('http://localhost:8000/login/', {
        username: us,
        password: pd,
      });

      if (response.status === 200) {
        this.setState({ submitForm: true });
        this.props.setIsAuthenticated(true);
        console.log("success");
      }
    } catch (error) {
      if (error.response && error.response.data) {
        const { status, data } = error.response;
        if (status === 400) {
          if (data.detail === "Invalid username or password") {
            this.setState({ showSubmitError: true, errorMsg: "Username or password is incorrect" });
          } else if (data.detail === "Invalid password") {
            this.setState({ showSubmitError: true, errorMsg: "Password is incorrect" });
          } else if (data.detail === "Invalid username") {
            this.setState({ showSubmitError: true, errorMsg: "Username is incorrect" });
          } else {
            this.setState({ showSubmitError: true, errorMsg: data.detail });
          }
        } else {
          this.setState({ showSubmitError: true, errorMsg: "An error occurred. Please try again." });
        }
      } else {
        this.setState({ showSubmitError: true, errorMsg: "*Invalid Username or Password" });
      }
      console.log("login failed");
    }
  };

  onEnterUsername = (event) => {
    this.setState({ us: event.target.value, usernameError: "" });
  };

  onChangePassword = (event) => {
    this.setState({ pd: event.target.value, passwordError: "" });
  };

  renderUsername = () => {
    const { us, usernameError } = this.state;

    return (
      <>
        <label className="label" htmlFor="userName">
          USERNAME
        </label>
        <input
          type="text"
          id="userName"
          placeholder="Username"
          className="user-input1"
          value={us}
          onChange={this.onEnterUsername}
          style={{ color: "white" }}
        />
        {usernameError && <p className="error-msg">{usernameError}</p>}
      </>
    );
  };

  renderPassword = () => {
    const { pd, passwordError } = this.state;

    return (
      <>
        <label className="label" htmlFor="password">
          PASSWORD
        </label>
        <input
          className="user-input1"
          id="password"
          type="password"
          placeholder="Password"
          value={pd}
          onChange={this.onChangePassword}
        />
        {passwordError && <p className="error-msg">{passwordError}</p>}
      </>
    );
  };

  render() {
    const { showSubmitError, errorMsg, submitForm } = this.state;
    console.log(submitForm);
    return (
      <div className="jobby-app-container">
        <div className="card">
          <img
            src="https://res.cloudinary.com/dlkovvlud/image/upload/v1699081090/Design_a_modern_and_captivating_logo_for_the_Gen-removebg-preview_2_vl2kfw.png"
            alt="website logo"
            className="website-logo"
          />
          <center>
            <h1 className="heading-login">Signin</h1>
          </center>
          <form className="form-container" onSubmit={this.onSubmitForm}>
            <div className="input-container">{this.renderUsername()}</div>
            <div className="input-container">{this.renderPassword()}</div>
            {showSubmitError && <p className="error-msg">{errorMsg}</p>}
            <button className="login-button" type="submit">
              Login
            </button>
            <div className="signupp">
              <p className="nothave">Don't have an account</p>
              <Link to="/signup">
                <p className="signup-para">Signup</p>
              </Link>
            </div>
          </form>
          {submitForm ? <Navigate to="/Home" /> : null}
        </div>
      </div>
    );
  }
}

export default SignIn;
